import React, {Component} from 'react';
import axios from 'axios';
import { Button, Form, FormGroup, FormLabel, FormControl, Label, Input, FormText, Table } from 'react-bootstrap';
import { Link } from 'react-router-dom';




const USER_SERVER_URL = 'https://group-project-airline.herokuapp.com/users.json';
class UserPage extends Component {
  constructor() {
    super();
    this.state = {
      users: []
    };

  this.saveUser = this.saveUser.bind(this);
  const fetchUser =()=>{
  	axios.get(USER_SERVER_URL).then((results)=>{
  		 console.log('fetchUsers',results.data);
  		this.setState({users: results.data});
  	 });
  }
  fetchUser();

}
//CHECK THIS SECTION
  saveUser (user) {
    console.log(user);
    axios.post(USER_SERVER_URL, user).then((result)=>{
      this.setState({users: [...this.state.users, result.data]});
    })
  }
  render(){
    return(
      <div>
        <UserSignUp onSubmit={ this.saveUser }/>
        <UserList users={ this.state.users }/>
      </div>
    );
  }
}

class UserSignUp extends Component {
  constructor() {
    super();
    this.state = {
      firstName: "",
      lastName: "",
      email: "",
      password: "",
    };

    this._handleFirstNameChange = this._handleFirstNameChange.bind(this);
    this._handleLastNameChange = this._handleLastNameChange.bind(this);
    this._handleEmailChange = this._handleEmailChange.bind(this);
    this._handlePasswordChange = this._handlePasswordChange.bind(this);
    this._handlePasswordConfirmationChange = this._handlePasswordConfirmationChange.bind(this);
    this._handleSubmit = this._handleSubmit.bind(this);

  }

  _handleFirstNameChange(event){
    this.setState( {firstName: event.target.value });
  }
  _handleLastNameChange(event){
    this.setState( {lastName: event.target.value });
  }
  _handleEmailChange(event){
    this.setState( {email: event.target.value });
  }
  _handlePasswordChange(event){
    this.setState( {password: event.target.value });
  }
  _handlePasswordConfirmationChange(event){
    this.setState( {passwordConfirmation: event.target.value });
  }

  _handleSubmit(event){
    event.preventDefault();
    const data = {first_name: this.state.firstName, last_name: this.state.lastName, email: this.state.email, isadmin: false, password: this.state.password };
    this.props.onSubmit( data );
    this.setState( {first_name: "", last_name: "", email: "", password: "" });
  }

  render() {
    return(
      <div>
        <h2>New Passenger</h2>

      <Form>
      <FormGroup controlId="formBasicEmail">

      <form onSubmit={this._handleSubmit}>

            <FormLabel>First Name: </FormLabel>
            <FormControl input type="text" placeholder="First Name" onChange={this._handleFirstNameChange} value={this.state.firstName} />

            <FormLabel>Last Name:</FormLabel>
            <FormControl input type="text" placeholder="Last Name" onChange={this._handleLastNameChange} value={this.state.lastName} />

            <FormLabel>Email Address:</FormLabel>
            <FormControl input type="email" placeholder="Email" onChange={this._handleEmailChange} value={this.state.email}/>

            <FormLabel>Password:</FormLabel>
            <FormControl input type="password" placeholder="Password" onChange={this._handlePasswordChange}  value={this.state.password}/>

            <FormLabel>Password Confirmation:</FormLabel>
            <FormControl input type="password" placeholder="Password Confirmation" onChange={this._handlePasswordConfirmationChange}  value={this.state.passwordConfirmation}/>

            <Button variant="primary" type="submit">
            Submit
            </Button>
            <Button variant="primary" type="reset" type="button" variant="primary" value="Cancel">
            Cancel
            </Button>

          </form>
          </FormGroup>
        </Form>
      </div>

    );
  }
}

class UserList extends Component {
  render() {
    return(
      <div>
      <h2>Passenger List</h2>
      <Table striped bordered hover size="sm">
          <thead>
              <tr>
                  <th>UserID</th>
                  <th>First Name</th>
                  <th>Last Name</th>
                  <th>Email</th>
              </tr>
          </thead>
          {
              this.props.users.map((user, index)=>
              <tbody key={user.id+1}>
                  <tr key={user.id}>
                      <td key={index+1}>{((user.id))}</td>
                      <td key={index+1}>{((user.first_name))}</td>
                      <td key={index+2}>{user.last_name}</td>
                      <td key={index+3}><Link to="/searchflights">{user.email}</Link></td>
                  </tr>
              </tbody>
              )
          }
      </Table>

      </div>
    )
  };
  }

export default UserPage;
