import React, { Component } from 'react';
import axios from 'axios';

const SERVER_URL = 'http://localhost:3000/airplanes.json';
class FlightPage extends Component {
  constructor() {
    super();
    this.state = {
    };
  }

  render() {
    return (
        <div>
            <h1>Virgin Airline</h1>
            <FlightForm />
        </div>
    );
  }
}

class FlightForm extends Component {
    constructor(){
        super();
        this.state = {
            airplanes: []
        };
    }
    loadAirplanes() {
        axios.get(SERVER_URL).then((result)=>{
            console.log(result);
        })
    };

    render() {
      return (
        <div>
            let airplanes = this.state.airplanes;
            let airplaneItems = airplanes.map((airplane)=>{
                <option key={airplanes.id}>{airplane.name}</option>
            })
            Flight Number: <input type="text" placeholder="QF746" required />
            Date: <input type="text" placeholder="DD/MM/YYYY" required />
            Origin: <input type="text" placeholder="Origin" required />
            Destination: <input type="text" placeholder="Destination" required />
            Plane: <select>
                {airplaneItems}
            </select>
            <input type="button" value="Cancel"></input>
            <input type="submit" value="Save"></input>
        </div>
      )
    };
}

export default FlightPage;
