import React, { Component } from 'react';
import axios from 'axios';

const SERVER_URL = 'http://localhost:3000/airplanes.json';
class FlightPage extends Component {
  constructor() {
    super();
    this.state = {
    };
  }

  render() {
    return (
        <div>
            <h1>Virgin Airline</h1>
            <FlightForm />
        </div>
    );
  }
}

class FlightForm extends Component {
    constructor(){
        super();
        this.state = {
            airplanes: [],
            flightNumber: "",
            departureDate: "",
            origin: "",
            destination: "",
            plane: ""
        };
        const loadAirplanes = () => {
            axios.get(SERVER_URL).then((result)=>{
                let airplanes = result.data.map((airplane)=>{
                    return <option key={airplane.id}>{airplane.name}</option>
                });
                this.setState({airplanes:airplanes});
            })
        };
        loadAirplanes();

        this._handleFlightNumberOnChange = this._handleFlightNumberOnChange.bind(this);
        this._handleDepartureDateOnChange = this._handleDepartureDateOnChange.bind(this);
        this._handlerOriginOnChange = this._handlerOriginOnChange.bind(this);
    }

    _handleFlightNumberOnChange(event) {
        this.setState({flightNumber:event.target.value});
    }

    _handleDepartureDateOnChange(event){
        this.setState({departureDate: event.target.value});
    }

    _handlerOriginOnChange(event){
        this.setState({origin: event.target.value});
    }

    render() {
      return (
        <div>
            Flight Number: <input type="text" placeholder="QF746" onChange={this._handleFlightNumberOnChange} required />
            Departure Date: <input type="text" placeholder="DD/MM/YYYY" onChange={this._handleDepartureDateOnChange} required />
            Origin: <input type="text" placeholder="Origin" onChange={this._handlerOriginOnChange} required />
            Destination: <input type="text" placeholder="Destination" required />
            Plane: <select>
                {this.state.airplanes}
            </select>
            <input type="button" value="Cancel"></input>
            <input type="submit" value="Save"></input>
        </div>
      )
    };
}

export default FlightPage;
