import React, {Component} from 'react';
import axios from 'axios';


class UserPage extends Component {
  constructor() {
    super();


  this.saveUser = this.saveUser.bind(this);
}

  saveUser (user) {
    const newUsers = this.state.users.slice(0);
    newUsers.push()
  }

  render(){
    return(
      <div>
      <h2>User Page</h2>
      <UserSignUp onSubmit={ this.saveUser }/>
      <UserLogIn />
      <UserList />
      </div>
    )
  }

}

class UserSignUp extends Component {
  constructor() {
    super();
    this.state = { user: {} };

    this._handleFirstNameChange = this._handleFirstNameChange.bind(this);
    this._handleLastNameChange = this._handleLastNameChange.bind(this);
    this._handleEmailChange = this._handleEmailChange.bind(this);
    this._handlePasswordChange = this._handlePasswordChange.bind(this);
    this._handlePasswordConfirmationChange = this._handlePasswordConfirmationChange.bind(this);
    this._handleSubmit = this._handleSubmit.bind(this);

  }

  _handleFirstNameChange(event){
    this.setState( {user: event.target.value });
  }
  _handleLastNameChange(event){
    this.setState( {user: event.target.value });
  }
  _handleEmailChange(event){
    this.setState( {user: event.target.value });
  }
  _handlePasswordChange(event){
    this.setState( {user: event.target.value });
  }
  _handlePasswordConfirmationChange(event){
    this.setState( {user: event.target.value });
  }

  _handleSubmit(event){
    event.preventDefault();
    this.props.onSubmit( this.state.content );
  }

  render() {
    return(
      <form onSubmit={this._handleSubmit}>
        First Name: <input type="text" placeholder="First Name" onChange={this._handleFirstNameChange}/>
        Last Name: <input type="text" placeholder="Last Name" onChange={this._handleLastNameChange} />
        Email Address: <input type="email" placeholder="Email" onChange={this._handleEmailChange} />
        Password: <input type="text" placeholder="Password" onChange={this._handlePasswordChange} />
        Password Confirmation: <input type="text" placeholder="Password Confirmation" onChange={this._handlePasswordConfirmationChange} />
        <input type="button" value="Cancel"></input>
        <input type="submit" value="Save"></input>
      </form>
    );
  }
}


class UserLogIn extends Component {
  render() {
    return(
      <h3>UserLogIn form coming soon</h3>
    );
  }
}


export default UserPage;
